import React,{useReducer} from 'react'
import css from "./index.css"

// <-----defining initial state and reducer----->
// here "state" is "current state" which is "initialstate" as of now
// as we  can have "action" of  different type (increment and decrement or any other action) in such situation we can use "switch case"
// <-----hierarchy of useReducer----->
// dispatch-->reducer-->state-->count-->display
//"dispatch"--> call "reducer function" and tell it what action should it  do, on the basics of the action -->"state " is updated and..
// updated value is return and store in --> "count" and diplay it
const initialstate=0;
const reducer=(state,action)=>{
  switch(action){
    case "increment": 
         return state+1
    case "decrement": 
          return state-1
    default : 
         return state

  }

}


export default function App() {
  //here"count" return current state and display it on the screen 
  const[count,dispatch]=useReducer(reducer, initialstate);
  return (
    <div className="mybox">
    <button className="butt" onClick={()=>dispatch("increment")}>+</button>
    <h1>{count}</h1>

    <button className="butt" onClick={()=>dispatch("decrement")}>-</button>

</div>
  )
}






