import React, { Component } from 'react'
import {MyContext} from "./App";
export default class GrandChild extends Component {
  render(){
   return (
      
     <>
     <h3>this is Grandchild component(Consumer component) </h3>

     <MyContext.Consumer>
     {myname=><h4>{myname}</h4>}{/* "myname" is function which is reciving data from provider */}
     
     
     </MyContext.Consumer>
     </>
    )
  }
}
