import React, { useEffect, useState } from "react";
import css from "./index.css";


function App(){


    const [count1 ,setCount1]=useState(0);
  
    function increment(){
        setCount1(count1+1);
    }
    const [count2 ,setCount2]=useState(50);
  
    function decrement(){
        setCount2(count2-1);
    }


useEffect(function(){
    console.log(" hellow this is useEffect called")
    // here this console is  called (1) when we render or execute the function and (2)every single time when we update it 
});

return(
    <>
        <div className="mybox">
            <h1>{count1}</h1>
            <button className="butt" onClick={increment}>click me</button>
        </div>
        <div className="mybox">
            <h1>{count2}</h1>
            <button className="butt" onClick={decrement}>click me</button>
        </div>
    </>
);
}


export default App;

