    function increment(){
      setcount(count+1);
  }  
   function decrement(){
    setcount(count-1);
}

  return (
    <>

    <div className="mybox">
        <button className="butt" onClick={increment}>+</button>
        <h1>{count}</h1>

        <button className="butt" onClick={decrement}>-</button>

    </div>
</>
  )
}
