import React from "react";



let currDate = new Date().getHours();
let gretting ="";
let mycss={};

    if(currDate>=1 && currDate<12){
        gretting ="Good Morning";
        mycss.color="red";
    }

    else if(currDate>=12 && currDate<19){
        gretting ="Good Evening";
        mycss.color="yellow";
    }

    else{
        gretting ="Good Night";
        mycss.color="green";

    }

    function chal4(){
        return(
         <h1 > Hello sameer,<span style={mycss}>{gretting}</span></h1> 


        )

    }

 export default chal4;
